import { useState } from "react"

export default function Mini({id,name,parag,count}) {
    const [position,setPosition] = useState('left')

    if(count == id){
    setPosition('center')
    }else if((count+1) == id){
    setPosition('left')
    }else if((count-1) == id){
    setPosition('right')
    }
  return (
    <>

<div>

<article className={`artic 
${position}
`} key={id}>
 <header>
  {name}
 </header>
<p>{parag}</p>
</article>

</div>

    </>
  )
}
