     if(count == 3){
      setCount(1)
    }else{
     setCount(count + 1)
    }
  },3000)

  return (<>
    <button className='btn p-2 b'>left</button>
<div className='BigDiv container'>

{Text.map(x=>{
const {id,name,parag} = x
return <article className={`artic  
