  setInterval(()=>{
     if(count == 3){
      setCount(1)
    }else{
     setCount(count + 1)
    }
  },1000)

  return (
<div className='BigDiv container'>
{Text.map(x=>{
const {id,name,parag} = x
return <article className={`artic  
${count == id && 'center'} ${(count+1) == id && 'left'} ${(count+1) == id && 'left'}
`} key={id}>
 <header>
  {name}
 </header>
<p>{parag}</p>
</article>
}
)
}

</div>
    );
}


